import React, { useState, useRef, useEffect } from 'react';
import { Mic, MicOff, Camera, Type, Maximize2, Minimize2, X, Scan } from 'lucide-react';
import axios from 'axios';
import { toast } from 'sonner';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const InterviewAssistant = () => {
  const [position, setPosition] = useState({ x: 100, y: 100 });
  const [size, setSize] = useState({ width: 450, height: 600 });
  const [opacity, setOpacity] = useState(0.95);
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [isMinimized, setIsMinimized] = useState(false);
  
  const [activeTab, setActiveTab] = useState('text');
  const [question, setQuestion] = useState('');
  const [solution, setSolution] = useState('');
  const [loading, setLoading] = useState(false);
  
  const [modelProvider, setModelProvider] = useState('openai');
  const [modelName, setModelName] = useState('gpt-5');
  
  const [isRecording, setIsRecording] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [isTakingScreenshot, setIsTakingScreenshot] = useState(false);
  
  const recognitionRef = useRef(null);
  const windowRef = useRef(null);
  const sessionId = useRef(`session_${Date.now()}`);

  // Initialize Speech Recognition
  useEffect(() => {
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = true;
      recognitionRef.current.interimResults = true;
      
      recognitionRef.current.onresult = (event) => {
        let interimTranscript = '';
        let finalTranscript = '';
        
        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcriptPiece = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            finalTranscript += transcriptPiece + ' ';
          } else {
            interimTranscript += transcriptPiece;
          }
        }
        
        setTranscript(prev => prev + finalTranscript);
      };
      
      recognitionRef.current.onerror = (event) => {
        console.error('Speech recognition error', event.error);
        toast.error('Speech recognition error: ' + event.error);
        setIsRecording(false);
      };
    }
  }, []);

  // Dragging handlers
  const handleMouseDown = (e) => {
    if (e.target.closest('.window-header')) {
      setIsDragging(true);
      setDragOffset({
        x: e.clientX - position.x,
        y: e.clientY - position.y
      });
    }
  };

  const handleMouseMove = (e) => {
    if (isDragging) {
      setPosition({
        x: e.clientX - dragOffset.x,
        y: e.clientY - dragOffset.y
      });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  useEffect(() => {
    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isDragging, dragOffset]);

  // Handle Screenshot
  const handleTakeScreenshot = async () => {
    try {
      setIsTakingScreenshot(true);
      toast.info('Select the window to capture...');
      
      // Request screen capture
      const stream = await navigator.mediaDevices.getDisplayMedia({
        video: { mediaSource: 'screen' }
      });
      
      // Create video element to capture frame
      const video = document.createElement('video');
      video.srcObject = stream;
      video.play();
      
      // Wait for video to be ready
      await new Promise(resolve => {
        video.onloadedmetadata = resolve;
      });
      
      // Capture screenshot
      const canvas = document.createElement('canvas');
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const ctx = canvas.getContext('2d');
      ctx.drawImage(video, 0, 0);
      
      // Stop the stream
      stream.getTracks().forEach(track => track.stop());
      
      // Convert to base64 and send to OCR
      const imageData = canvas.toDataURL('image/png');
      
      const response = await axios.post(`${API}/ocr-image`, {
        image_base64: imageData
      });
      
      setQuestion(response.data.extracted_text);
      toast.success('Screenshot captured and text extracted!');
      setIsTakingScreenshot(false);
    } catch (error) {
      console.error('Screenshot error:', error);
      toast.error('Failed to capture screenshot: ' + error.message);
      setIsTakingScreenshot(false);
    }
  };

  // Handle Screen Scan
  const handleScreenScan = async () => {
    try {
      setIsScanning(true);
      toast.info('Select the screen/window to scan...');
      
      // Request screen capture
      const stream = await navigator.mediaDevices.getDisplayMedia({
        video: { mediaSource: 'screen' }
      });
      
      // Create video element to capture frame
      const video = document.createElement('video');
      video.srcObject = stream;
      video.play();
      
      // Wait for video to be ready
      await new Promise(resolve => {
        video.onloadedmetadata = resolve;
      });
      
      // Capture screenshot
      const canvas = document.createElement('canvas');
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const ctx = canvas.getContext('2d');
      ctx.drawImage(video, 0, 0);
      
      // Stop the stream
      stream.getTracks().forEach(track => track.stop());
      
      // Convert to base64 and send to OCR
      const imageData = canvas.toDataURL('image/png');
      
      const response = await axios.post(`${API}/ocr-image`, {
        image_base64: imageData
      });
      
      setQuestion(response.data.extracted_text);
      toast.success('Screen scanned and text extracted!');
      setIsScanning(false);
    } catch (error) {
      console.error('Screen scan error:', error);
      toast.error('Failed to scan screen: ' + error.message);
      setIsScanning(false);
    }
  };

  // Handle audio recording
  const toggleRecording = () => {
    if (!recognitionRef.current) {
      toast.error('Speech recognition not supported in this browser');
      return;
    }

    if (isRecording) {
      recognitionRef.current.stop();
      setIsRecording(false);
      if (transcript) {
        setQuestion(transcript);
      }
    } else {
      setTranscript('');
      recognitionRef.current.start();
      setIsRecording(true);
      toast.success('Listening...');
    }
  };

  // Solve the coding problem
  const handleSolve = async () => {
    if (!question.trim()) {
      toast.error('Please enter a question');
      return;
    }

    setLoading(true);
    setSolution('');

    try {
      const response = await axios.post(`${API}/solve-code`, {
        question: question.trim(),
        model_provider: modelProvider,
        model_name: modelName,
        session_id: sessionId.current
      });
      
      setSolution(response.data.solution);
      toast.success('Solution generated!');
    } catch (error) {
      console.error('Error solving problem:', error);
      toast.error('Failed to generate solution: ' + (error.response?.data?.detail || error.message));
    } finally {
      setLoading(false);
    }
  };

  // Copy solution to clipboard
  const handleCopy = () => {
    navigator.clipboard.writeText(solution);
    toast.success('Copied to clipboard!');
  };

  const modelOptions = {
    openai: ['gpt-5', 'gpt-5-mini', 'gpt-4o', 'gpt-4o-mini'],
    anthropic: ['claude-4-sonnet-20250514', 'claude-3-7-sonnet-20250219', 'claude-3-5-sonnet-20241022'],
    gemini: ['gemini-2.5-pro', 'gemini-2.5-flash', 'gemini-2.0-flash'],
    deepseek: ['deepseek-chat', 'deepseek-coder', 'deepseek-r1']
  };

  if (isMinimized) {
    return (
      <div
        className="draggable-window"
        style={{
          left: `${position.x}px`,
          top: `${position.y}px`,
          width: '200px',
          height: '50px',
          opacity: opacity
        }}
        onMouseDown={handleMouseDown}
      >
        <div className="window-header">
          <div className="window-title">Code Assistant</div>
          <div className="window-controls">
            <button className="control-btn" onClick={() => setIsMinimized(false)} data-testid="maximize-btn">
              <Maximize2 size={16} />
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div
      ref={windowRef}
      className="draggable-window"
      style={{
        left: `${position.x}px`,
        top: `${position.y}px`,
        width: `${size.width}px`,
        height: `${size.height}px`,
        opacity: opacity
      }}
      onMouseDown={handleMouseDown}
    >
      <div className="window-header">
        <div className="window-title">
          <span>ðŸŽ¯</span>
          Code Assistant
        </div>
        <div className="window-controls">
          <div className="transparency-control">
            <input
              type="range"
              min="0.3"
              max="1"
              step="0.1"
              value={opacity}
              onChange={(e) => setOpacity(parseFloat(e.target.value))}
              className="transparency-slider"
              data-testid="opacity-slider"
            />
          </div>
          <button className="control-btn" onClick={() => setIsMinimized(true)} data-testid="minimize-btn">
            <Minimize2 size={16} />
          </button>
          <button className="control-btn" onClick={() => window.location.reload()} data-testid="close-btn">
            <X size={16} />
          </button>
        </div>
      </div>

      <div className="window-content">
        {/* Input Tabs */}
        <div className="input-section">
          <div className="tabs">
            <button
              className={`tab-btn ${activeTab === 'text' ? 'active' : ''}`}
              onClick={() => setActiveTab('text')}
              data-testid="text-tab"
            >
              <Type size={16} /> Text
            </button>
            <button
              className={`tab-btn ${activeTab === 'screenshot' ? 'active' : ''}`}
              onClick={() => setActiveTab('screenshot')}
              data-testid="screenshot-tab"
            >
              <Camera size={16} /> Screenshot
            </button>
            <button
              className={`tab-btn ${activeTab === 'voice' ? 'active' : ''}`}
              onClick={() => setActiveTab('voice')}
              data-testid="voice-tab"
            >
              {isRecording ? <MicOff size={16} /> : <Mic size={16} />} Voice
            </button>
            <button
              className={`tab-btn ${activeTab === 'scan' ? 'active' : ''}`}
              onClick={() => setActiveTab('scan')}
              data-testid="scan-tab"
            >
              <Scan size={16} /> Scan
            </button>
          </div>

          {/* Text Input */}
          {activeTab === 'text' && (
            <div className="input-container">
              <textarea
                className="textarea-input"
                placeholder="Paste your coding question here..."
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                data-testid="question-textarea"
              />
            </div>
          )}

          {/* Screenshot */}
          {activeTab === 'screenshot' && (
            <div className="input-container">
              <div className="screenshot-container">
                <button
                  className="screenshot-btn"
                  onClick={handleTakeScreenshot}
                  disabled={isTakingScreenshot}
                  data-testid="take-screenshot-btn"
                >
                  {isTakingScreenshot ? (
                    <>
                      <div className="spinner" />
                      Capturing...
                    </>
                  ) : (
                    <>
                      <Camera size={32} />
                      <div className="screenshot-text">Take Screenshot</div>
                      <div className="screenshot-hint">Capture coding question</div>
                    </>
                  )}
                </button>
              </div>
              {question && (
                <textarea
                  className="textarea-input"
                  style={{ marginTop: '12px' }}
                  value={question}
                  onChange={(e) => setQuestion(e.target.value)}
                  data-testid="screenshot-text-textarea"
                />
              )}
            </div>
          )}

          {/* Voice Recording */}
          {activeTab === 'voice' && (
            <div className="input-container">
              <div className="audio-controls">
                <button
                  className={`record-btn ${isRecording ? 'recording' : ''}`}
                  onClick={toggleRecording}
                  data-testid="record-btn"
                >
                  {isRecording ? (
                    <>
                      <MicOff size={16} />
                      Stop Recording
                    </>
                  ) : (
                    <>
                      <Mic size={16} />
                      Start Recording
                    </>
                  )}
                </button>
              </div>
              {(transcript || question) && (
                <div className="transcript-box" data-testid="transcript-box">
                  {isRecording ? transcript : question}
                </div>
              )}
            </div>
          )}

          {/* Screen Scan */}
          {activeTab === 'scan' && (
            <div className="input-container">
              <div className="scan-container">
                <button
                  className="scan-btn"
                  onClick={handleScreenScan}
                  disabled={isScanning}
                  data-testid="scan-screen-btn"
                >
                  {isScanning ? (
                    <>
                      <div className="spinner" />
                      Scanning...
                    </>
                  ) : (
                    <>
                      <Scan size={32} />
                      <div className="scan-text">Scan Screen</div>
                      <div className="scan-hint">Capture entire screen or window</div>
                    </>
                  )}
                </button>
              </div>
              {question && (
                <textarea
                  className="textarea-input"
                  style={{ marginTop: '12px' }}
                  value={question}
                  onChange={(e) => setQuestion(e.target.value)}
                  data-testid="scanned-text-textarea"
                />
              )}
            </div>
          )}
        </div>

        {/* Model Selector */}
        <div className="model-selector">
          <select
            className="model-select"
            value={modelProvider}
            onChange={(e) => {
              setModelProvider(e.target.value);
              setModelName(modelOptions[e.target.value][0]);
            }}
            data-testid="provider-select"
          >
            <option value="openai">OpenAI</option>
            <option value="anthropic">Anthropic</option>
            <option value="gemini">Google Gemini</option>
            <option value="deepseek">DeepSeek</option>
          </select>
          <select
            className="model-select"
            value={modelName}
            onChange={(e) => setModelName(e.target.value)}
            data-testid="model-select"
          >
            {modelOptions[modelProvider].map(model => (
              <option key={model} value={model}>{model}</option>
            ))}
          </select>
        </div>

        {/* Solve Button */}
        <button
          className="solve-btn"
          onClick={handleSolve}
          disabled={loading}
          data-testid="solve-btn"
        >
          {loading ? (
            <>
              <div className="spinner" />
              Generating Solution...
            </>
          ) : (
            'âš¡ Generate Solution'
          )}
        </button>

        {/* Output Section */}
        {solution && (
          <div className="output-section" data-testid="output-section">
            <div className="output-header">
              <div className="output-title">Solution</div>
              <button className="copy-btn" onClick={handleCopy} data-testid="copy-btn">
                Copy
              </button>
            </div>
            <div className="solution-text" data-testid="solution-text">{solution}</div>
          </div>
        )}

        {!solution && !loading && (
          <div className="no-solution">Enter a question and click Generate Solution</div>
        )}
      </div>
    </div>
  );
};

export default InterviewAssistant;
